function submitForm(e){e.preventDefault();let l=document.querySelector("#name").value,t=document.querySelector("#phone").value,o=document.querySelector("#email").value,r=document.querySelector("#neighborhood").value,a=document.querySelector("#city").value;var n=document.getElementById("alertMsg");let c={name:l,phone:t.replace("(","").replace(")","").replace("-",""),email:o,neighborhood:r,city:a};fetch("https://verly-leads-api.loca.lt/verly-leads-api/send",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(c)}).then(e=>{alertSuccessMsg.style.display="block",setTimeout(function(){alertSuccessMsg.style.display="none"},3e3),document.getElementById("contact-form").reset(),n.focus(),console.log("Post created successfully!")}).catch(e=>{alertErrorMsg.style.display="block",n.focus(),setTimeout(function(){alertErrorMsg.style.display="none"},3e3),console.error("Error creating post:",e)})}