function submitForm(e){e.preventDefault();let t=document.querySelector("#name").value,r=document.querySelector("#phone").value,o=document.querySelector("#email").value,l=document.querySelector("#neighborhood").value,a=document.querySelector("#city").value,c={name:t,phone:r.replace("(","").replace(")",""),email:o,neighborhood:l,city:a};fetch("https://verly-leads-api.loca.lt/verly-leads-api/send",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(c)}).then(e=>{document.getElementById("contact-form").reset(),console.log("Post created successfully!")}).catch(e=>{console.error("Error creating post:",e)})}