function submitForm(e){e.preventDefault();let t=document.querySelector("#name").value,l=document.querySelector("#phone").value,o=document.querySelector("#email").value,r=document.querySelector("#neighborhood").value,a=document.querySelector("#city").value;document.getElementById("alertMsg");let n={name:t,phone:l.replace("(","").replace(")",""),email:o,neighborhood:r,city:a};fetch("https://verly-leads-api.loca.lt/verly-leads-api/send",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(n)}).then(e=>{alertSuccessMsg.style.display="block",setTimeout(function(){alertSuccessMsg.style.display="none"},3e3),document.getElementById("contact-form").reset(),console.log("Post created successfully!")}).catch(e=>{alertErrorMsg.style.display="block",setTimeout(function(){alertErrorMsg.style.display="none"},3e3),console.error("Error creating post:",e)})}