function submitForm(e){e.preventDefault();let l=document.querySelector("#name").value,t=document.querySelector("#phone").value,o=document.querySelector("#email").value,r=document.querySelector("#neighborhood").value,a=document.querySelector("#city").value;document.getElementById("alertMsg");let n={name:l,phone:t.replace("(","").replace(")","").replace("-",""),email:o,neighborhood:r,city:a};fetch("https://verly-leads-api.loca.lt-v2/verly-leads-api/send",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(n)}).then(e=>{alertSuccessMsg.style.display="block",setTimeout(function(){alertSuccessMsg.style.display="none"},5e3),document.getElementById("contact-form").reset(),document.querySelector("#name").focus(),console.log("Post created successfully!")}).catch(e=>{alertErrorMsg.style.display="block",document.querySelector("#name").focus(),setTimeout(function(){alertErrorMsg.style.display="none"},5e3),console.error("Error creating post:",e)})}